mapper.select:
select org_groupname, org_teamname as emp_dept, emp_id, emp_name, emp_posi, emp_sal,
	cast(floor(emp_sal/12) as char) as emp_pay, emp_join, emp_resi from employee e 
inner join organization o on o.org_teamcode = e.emp_dept;

mapper.update:
update employee 
set 
	emp_pw = #{emp_pw} 
where emp_id = #{emp_id}

update.foreach:
update employee
 emp_dept = #{info.emp_dept}, emp_name = #{info.emp_name}, emp_posi = #{info.emp_posi}, emp_tel = #{info.emp_tel}, emp_email = #{info.emp_email}, emp_level = #{info.emp_level} 
where emp_id = #{info.emp_id}

mapper.update:
update employee set emp_pw=#{emp_pw}, emp_email=#{emp_email},
emp_pic=#{emp_pic},
emp_tel=#{emp_tel} where emp_id=#{emp_id}
